try {
  const allInputs = $input.all();
  if (!allInputs || allInputs.length === 0) {
    throw new Error('No input data available');
  }
  const inputData = allInputs[0];

  let surveyData = [];
  let dataSource = 'unknown';
  let processingMode = 'webhook';
  let validationErrors = [];

  if (inputData.httpMethod === 'POST') {
    processingMode = 'webhook';

    if (!inputData.body) {
      validationErrors.push('Missing request body');
    } else {
      if (inputData.body.form_response) {
        dataSource = 'typeform';
        const response = inputData.body.form_response;

        if (!response.token) validationErrors.push('Missing Typeform token');
        if (!response.submitted_at) validationErrors.push('Missing submission timestamp');

        if (validationErrors.length === 0) {
          surveyData = [{
            id: response.token,
            submitted_at: response.submitted_at,
            answers: response.answers,
            source: 'typeform',
            raw_data: response
          }];
        }
      } else {
        dataSource = 'generic_form';
        const randomId = 'gen_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
        surveyData = [{
          id: randomId,
          submitted_at: new Date().toISOString(),
          answers: inputData.body,
          source: 'generic_form',
          raw_data: inputData.body
        }];
      }
    }
  } else {
    processingMode = 'scheduled';
    dataSource = 'scheduled_fetch';
    surveyData = [];
  }

  const batchId = 'batch_' + Date.now();
  const result = {
    processing_mode: processingMode,
    data_source: dataSource,
    response_count: surveyData.length,
    survey_data: surveyData,
    processing_timestamp: new Date().toISOString(),
    batch_id: batchId,
    validation_errors: validationErrors,
    validation_status: validationErrors.length === 0 ? 'valid' : 'invalid'
  };

  return [{ json: result }];

} catch (error) {
  return [{ json: {
    error: true,
    error_message: error.message,
    processing_mode: 'error',
    validation_status: 'error'
  } }];
}

try {
  const inputData = $input.all()[0].json;
  let surveyData = [];

  if (inputData.survey_data && inputData.survey_data.length > 0) {
    surveyData = inputData.survey_data;
  } else if (Array.isArray(inputData)) {
    surveyData = inputData.map(row => ({
      id: row.response_id,
      submitted_at: row.submitted_at,
      source: row.source_platform,
      answers: typeof row.raw_data === 'string' ? JSON.parse(row.raw_data) : row.raw_data,
      database_id: row.id
    }));
  }

  if (surveyData.length === 0) {
    return [{ json: { text_extracts: [], extraction_summary: { total_responses: 0 } } }];
  }

  const textExtracts = [];

  surveyData.forEach((response, index) => {
    const answers = response.answers || {};
    let extractedTexts = [];

    if (Array.isArray(answers)) {
      answers.forEach(answer => {
        if (answer.text && answer.text.length > 5) {
          const fieldRef = answer.field && answer.field.ref ? answer.field.ref : (answer.field && answer.field.id ? answer.field.id : 'unknown');
          extractedTexts.push({
            field: fieldRef,
            text: answer.text,
            length: answer.text.length
          });
        }
      });
    } else if (typeof answers === 'object') {
      Object.keys(answers).forEach(key => {
        const value = answers[key];
        if (typeof value === 'string' && value.length > 5) {
          extractedTexts.push({
            field: key,
            text: value,
            length: value.length
          });
        }
      });
    }

    if (extractedTexts.length > 0) {
      textExtracts.push({
        response_id: response.id,
        database_id: response.database_id,
        submitted_at: response.submitted_at,
        source: response.source,
        texts: extractedTexts,
        combined_text: extractedTexts.map(t => t.text).join(' '),
        total_length: extractedTexts.reduce((sum, t) => sum + t.length, 0)
      });
    }
  });

  const result = Object.assign({}, inputData, {
    text_extracts: textExtracts,
    extraction_summary: {
      total_responses: surveyData.length,
      responses_with_text: textExtracts.length,
      total_text_length: textExtracts.reduce((sum, t) => sum + t.total_length, 0)
    }
  });

  return [{ json: result }];

} catch (error) {
  return [{ json: { error: true, error_message: error.message, text_extracts: [] } }];
}

try {
  const allInputs = $input.all();
  if (!allInputs || allInputs.length === 0) {
    throw new Error('No sentiment analysis results available');
  }

  let openaiResults = null;
  let huggingfaceResults = null;
  let originalData = null;

  // Process inputs from both sentiment analysis services
  allInputs.forEach(input => {
    const data = input.json;

    // Check if this is OpenAI response
    if (data.choices && data.choices[0] && data.choices[0].message) {
      try {
        const content = data.choices[0].message.content;
        openaiResults = typeof content === 'string' ? JSON.parse(content) : content;
      } catch (e) {
        console.log('Failed to parse OpenAI response:', e.message);
      }
    }

    // Check if this is HuggingFace response
    else if (Array.isArray(data) && data[0] && Array.isArray(data[0])) {
      huggingfaceResults = data;
    }

    // Check if this contains original survey data
    else if (data.text_extracts || data.extraction_summary) {
      originalData = data;
    }
  });

  // Get original data from the first input if not found
  if (!originalData && allInputs[0].json.text_extracts) {
    originalData = allInputs[0].json;
  }

  if (!originalData || !originalData.text_extracts) {
    throw new Error('Original survey data not found in inputs');
  }

  const processedResults = [];
  const textExtracts = originalData.text_extracts;

  textExtracts.forEach((extract, index) => {
    let combinedSentiment = {
      response_id: extract.response_id,
      database_id: extract.database_id,
      submitted_at: extract.submitted_at,
      source: extract.source,
      text_length: extract.total_length,
      analysis_timestamp: new Date().toISOString()
    };

    // Process OpenAI results
    if (openaiResults && Array.isArray(openaiResults)) {
      const openaiResult = openaiResults.find(r => r.id === extract.response_id);
      if (openaiResult) {
        combinedSentiment.openai = {
          sentiment: openaiResult.sentiment || 'neutral',
          score: parseFloat(openaiResult.score) || 0,
          urgency: openaiResult.urgency || 'low',
          confidence: openaiResult.confidence || 0.5
        };
      }
    }

    // Process HuggingFace results
    if (huggingfaceResults && huggingfaceResults[index]) {
      const hfResult = huggingfaceResults[index];
      if (Array.isArray(hfResult)) {
        const topResult = hfResult.reduce((prev, current) => 
          (prev.score > current.score) ? prev : current
        );

        let normalizedSentiment = 'neutral';
        let normalizedScore = 0;

        if (topResult.label === 'LABEL_2' || topResult.label.toLowerCase().includes('pos')) {
          normalizedSentiment = 'positive';
          normalizedScore = topResult.score;
        } else if (topResult.label === 'LABEL_0' || topResult.label.toLowerCase().includes('neg')) {
          normalizedSentiment = 'negative';
          normalizedScore = -topResult.score;
        }

        combinedSentiment.huggingface = {
          sentiment: normalizedSentiment,
          score: normalizedScore,
          confidence: topResult.score,
          raw_label: topResult.label
        };
      }
    }

    // Calculate consensus sentiment
    const sentiments = [];
    const scores = [];

    if (combinedSentiment.openai) {
      sentiments.push(combinedSentiment.openai.sentiment);
      scores.push(combinedSentiment.openai.score);
    }

    if (combinedSentiment.huggingface) {
      sentiments.push(combinedSentiment.huggingface.sentiment);
      scores.push(combinedSentiment.huggingface.score);
    }

    // Determine consensus
    let consensusSentiment = 'neutral';
    let consensusScore = 0;
    let consensusConfidence = 0;

    if (scores.length > 0) {
      consensusScore = scores.reduce((a, b) => a + b, 0) / scores.length;

      if (consensusScore > 0.1) {
        consensusSentiment = 'positive';
      } else if (consensusScore < -0.1) {
        consensusSentiment = 'negative';
      }

      // Calculate confidence based on agreement
      const posCount = sentiments.filter(s => s === 'positive').length;
      const negCount = sentiments.filter(s => s === 'negative').length;
      const neuCount = sentiments.filter(s => s === 'neutral').length;

      const maxCount = Math.max(posCount, negCount, neuCount);
      consensusConfidence = maxCount / sentiments.length;
    }

    combinedSentiment.consensus = {
      sentiment: consensusSentiment,
      score: consensusScore,
      confidence: consensusConfidence,
      agreement_level: consensusConfidence >= 0.8 ? 'high' : consensusConfidence >= 0.6 ? 'medium' : 'low'
    };

    // Determine urgency based on sentiment and confidence
    let urgency = 'low';
    if (Math.abs(consensusScore) > 0.7 && consensusConfidence > 0.7) {
      urgency = consensusSentiment === 'negative' ? 'high' : 'medium';
    } else if (Math.abs(consensusScore) > 0.4) {
      urgency = 'medium';
    }

    combinedSentiment.urgency = urgency;
    combinedSentiment.requires_attention = urgency === 'high' || (consensusSentiment === 'negative' && consensusConfidence > 0.6);

    processedResults.push(combinedSentiment);
  });

  // Calculate batch statistics
  const batchStats = {
    total_responses: processedResults.length,
    sentiment_distribution: {
      positive: processedResults.filter(r => r.consensus.sentiment === 'positive').length,
      negative: processedResults.filter(r => r.consensus.sentiment === 'negative').length,
      neutral: processedResults.filter(r => r.consensus.sentiment === 'neutral').length
    },
    urgency_distribution: {
      high: processedResults.filter(r => r.urgency === 'high').length,
      medium: processedResults.filter(r => r.urgency === 'medium').length,
      low: processedResults.filter(r => r.urgency === 'low').length
    },
    average_sentiment_score: processedResults.length > 0 ? 
      processedResults.reduce((sum, r) => sum + r.consensus.score, 0) / processedResults.length : 0,
    high_confidence_results: processedResults.filter(r => r.consensus.confidence > 0.7).length,
    attention_required: processedResults.filter(r => r.requires_attention).length
  };

  const result = Object.assign({}, originalData, {
    sentiment_results: processedResults,
    batch_statistics: batchStats,
    processing_complete: true,
    analysis_timestamp: new Date().toISOString(),
    services_used: {
      openai: !!openaiResults,
      huggingface: !!huggingfaceResults
    }
  });

  return [{ json: result }];

} catch (error) {
  return [{ json: {
    error: true,
    error_message: error.message,
    sentiment_results: [],
    processing_complete: false
  } }];
}
